    private void PayMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_PayMouseClicked

        String value1 = nama.getText();
        String value2 = kelas.getText();
        String value3 = nomesin.getText();
        String value4 = paket.getText();
        String value5 = vanish.getText();
        String value6 = totalharga.getText();
        String value7 = Bayar.getText();
        String value8 = kembalian.getText();
        String value9 = tanggal.getDateFormatString();
        String value10 = hp.getText();
        String value11 = note.getText();


        try{
            //             String sql = "INSERT INTO datapayment22 VALUES ('"+nama.getText()+"', '"+kelas.getText()+"', '"+nomesin.getText()+"', '"+paket.getText()+"', '"+vanish.getText()+"', '"+totalharga.getText()+"', '"+Bayar.getText()+"', '"+kembalian.getText()+"', '"+tanggal.getDateFormatString()+"', '"+hp.getText()+"', '"+note.getText()+"')";
            con1 = DriverManager.getConnection("jdbc:mysql://localhost/pdlaundry","root","");
            java.sql.PreparedStatement pst = con1.prepareStatement("INSERT into `datapayment22` (`Name`,`Class`,`Washing Machine`,`Wash Package`,`Soap`,`Total Price`,`Paid`,`Change`,`Date`,`Phone`,`Note`)VALUES(?,?,?,?,?,?,?,?,?,?,?)");
            pst.setString(1,value1);
            pst.setString(2,value2);
            pst.setString(3,value3);
            pst.setString(4,value4);
            pst.setString(5,value5);
            pst.setString(6,value6);
            pst.setString(7,value7);
            pst.setString(8,value8);
            pst.setString(9,value9);
            pst.setString(10,value10);
            pst.setString(11,value11);
            pst.execute();
            JOptionPane.showMessageDialog(null, "Transaction succes");
        }catch (Exception e){
            JOptionPane.showMessageDialog(this, e.getMessage());
        }
    }//GEN-LAST:event_PayMouseClicked

