    private void vanishKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_vanishKeyReleased
        int berat = Integer.parseInt(hargacuci.getText());
        int vanish1 = Integer.parseInt(vanish.getText());
        int vanish2 = vanish1 * 2500;
        hargasabun.setText(String.valueOf(vanish2));

        total = berat+vanish2;
        totalharga.setText(String.valueOf(total));
    }//GEN-LAST:event_vanishKeyReleased

    private void paketKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_paketKeyReleased
        int berat = Integer.parseInt(paket.getText());
        int harga = berat * 15000;
        hargacuci.setText(String.valueOf(harga));
    }//GEN-LAST:event_paketKeyReleased

